import { sleep } from './sleep.js';
export function retryAsync(fn, attempts = 3, backoffBaseMs = 200) {
    return async (...args) => {
        let lastError;
        for (let attempt = 0; attempt < attempts; attempt++) {
            try {
                return await fn(...args);
            }
            catch (error) {
                lastError = error;
                if (attempt < attempts) {
                    await sleep(backoffBaseMs * (attempt + 1));
                }
            }
        }
        throw lastError;
    };
}
